sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    // Add your code here

    var requestedForEmail = current.opened_for.email.toString();
    var requestedForName = current.opened_for.first_name.toString();
    email.addAddress('cc', requestedForEmail, requestedForName);

    var assignmentGroupEmail = current.assignment_group.email.toString();
    var assignmentGroupName = current.assignment_group.name.toString();
    email.addAddress('cc', assignmentGroupEmail, assignmentGroupName);





})(current, template, email, email_action, event);
