sys_script_include.script:
var AssignedToFieldValue = Class.create();

AssignedToFieldValue.prototype = {

    initialize: function() { },

    assignTo: function(a) {

        var user = [];

        var grp = new GlideRecord('sys_user_grmember');

        if (!a) {
            grp.addEncodedQuery('groupLIKEfinance');
            grp.query();

            while (grp.next())

                user.push(grp.user.toString());

        } else {
            grp.addQuery('group', a);
            grp.query();

            while (grp.next())

                user.push(grp.user.toString());
        }

        return 'sys_idIN' + user.join(',');


    },

    type: 'AssignedToFieldValue'
};
