sys_script_include.script:
var frAjax = Class.create();
frAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getUserGroups: function() {
        var group = this.getParameter("sysparm_group");
        var user = this.getParameter("sysparm_user");
        var result = false;

        var groupLookupGr = new GlideRecord('sys_user_grmember');

        groupLookupGr.addQuery('group.sys_id', group);
        groupLookupGr.addQuery('user', user);
        groupLookupGr.setLimit(1);
        groupLookupGr.query();

        if (groupLookupGr.hasNext()) {
            result = true;
        }

        return result;

    },
    type: 'frAjax'
});
